create table activities_v2
(
    id              bigint unsigned auto_increment
        primary key,
    title           longtext        null,
    content         longtext        null,
    image_path      longtext        null,
    module_id       bigint unsigned null,
    `order`         bigint unsigned null,
    points          bigint unsigned null,
    habit_threshold bigint unsigned null,
    recurrence      bigint unsigned null,
    created_at      datetime(3)     null,
    updated_at      datetime(3)     null,
    deleted_at      datetime(3)     null
);

create index idx_activities_v2_id
    on activities_v2 (id);

create index idx_activities_v2_module_id
    on activities_v2 (module_id);

